import React, { useState } from 'react';
import MainLayout from '../components/Layout/MainLayout';
import {
  PlusIcon,
  MagnifyingGlassIcon,
  ShieldExclamationIcon,
  ArrowPathIcon,
  TrashIcon,
} from '@heroicons/react/24/outline';

interface SocialAccount {
  id: string;
  platform: string;
  username: string;
  email: string;
  lastChecked: string;
  status: 'secure' | 'warning' | 'breached';
}

const mockAccounts: SocialAccount[] = [
  {
    id: '1',
    platform: 'Instagram',
    username: '@user123',
    email: 'user@example.com',
    lastChecked: '2 hours ago',
    status: 'secure',
  },
  {
    id: '2',
    platform: 'Twitter',
    username: '@tweeter',
    email: 'social@example.com',
    lastChecked: '1 day ago',
    status: 'warning',
  },
  {
    id: '3',
    platform: 'LinkedIn',
    username: 'Professional User',
    email: 'work@example.com',
    lastChecked: '3 days ago',
    status: 'breached',
  },
];

const statusColors = {
  secure: 'bg-green-100 text-green-800',
  warning: 'bg-yellow-100 text-yellow-800',
  breached: 'bg-red-100 text-red-800',
};

const Social = () => {
  const [accounts, setAccounts] = useState<SocialAccount[]>(mockAccounts);
  const [searchQuery, setSearchQuery] = useState('');

  const filteredAccounts = accounts.filter(
    (a) =>
      a.platform.toLowerCase().includes(searchQuery.toLowerCase()) ||
      a.username.toLowerCase().includes(searchQuery.toLowerCase()) ||
      a.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <MainLayout>
      <div className="space-y-6">
        <div className="sm:flex sm:items-center sm:justify-between">
          <div>
            <h1 className="text-2xl font-semibold text-gray-900">Social Accounts</h1>
            <p className="mt-2 text-sm text-gray-700">
              Monitor and secure your social media accounts.
            </p>
          </div>
          <div className="mt-4 sm:mt-0">
            <button
              type="button"
              className="inline-flex items-center rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
              <PlusIcon className="-ml-0.5 mr-1.5 h-5 w-5" aria-hidden="true" />
              Add Account
            </button>
          </div>
        </div>

        {/* Search */}
        <div className="relative">
          <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
            <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
          </div>
          <input
            type="text"
            className="block w-full rounded-md border-0 py-1.5 pl-10 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            placeholder="Search accounts..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        {/* Account List */}
        <div className="overflow-hidden rounded-lg bg-white shadow">
          <ul role="list" className="divide-y divide-gray-200">
            {filteredAccounts.map((account) => (
              <li key={account.id} className="px-4 py-4 sm:px-6">
                <div className="flex items-center justify-between">
                  <div className="flex min-w-0 flex-1 items-center">
                    <div className="min-w-0 flex-1">
                      <div className="flex items-center justify-between">
                        <p className="truncate text-sm font-medium text-indigo-600">
                          {account.platform}
                        </p>
                        <div className="ml-2 flex flex-shrink-0">
                          <p
                            className={`inline-flex rounded-full px-2 text-xs font-semibold leading-5 ${
                              statusColors[account.status]
                            }`}
                          >
                            {account.status.charAt(0).toUpperCase() + account.status.slice(1)}
                          </p>
                        </div>
                      </div>
                      <div className="mt-2 flex">
                        <div className="flex items-center text-sm text-gray-500">
                          <p>{account.username}</p>
                          <span className="mx-2">â€¢</span>
                          <p>{account.email}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="ml-4 flex flex-shrink-0 space-x-2">
                    <button
                      type="button"
                      className="inline-flex items-center rounded-md bg-white px-2.5 py-1.5 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                    >
                      <ShieldExclamationIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                    </button>
                    <button
                      type="button"
                      className="inline-flex items-center rounded-md bg-white px-2.5 py-1.5 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                    >
                      <ArrowPathIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                    </button>
                    <button
                      type="button"
                      className="inline-flex items-center rounded-md bg-white px-2.5 py-1.5 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50"
                    >
                      <TrashIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                    </button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </MainLayout>
  );
};

export default Social;
