import axios from 'axios';
import { User, Password, SocialAccount, BreachAlert, SecureNote, SharedSecret, UserLogin, UserRegister, Token, UserUpdate, UserPasswordUpdate } from '../types';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:8000';

export const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Add a request interceptor to add the auth token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add a response interceptor to handle common errors
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Handle unauthorized access
      localStorage.removeItem('token');
      window.location.href = '/login';
    }
    return Promise.reject(error);
  }
);

export const checkPasswordsForBreach = () => api.post<BreachAlert[]>('/api/v1/breach/check-passwords');
export const checkEmailForBreach = () => api.post<BreachAlert[]>('/api/v1/breach/check-email');
export const getBreachAlerts = () => api.get<BreachAlert[]>('/api/v1/breach/alerts');
export const resolveBreachAlert = (alertId: string) => api.put<BreachAlert>(`/api/v1/breach/alerts/${alertId}/resolve`);
